# 4.2: how do bacteria make antibiotics
# Protein Translation Problem: Translate an RNA string into an amino acid string.

def translate(pattern, geneticCode):
    '''
    :params RNA: RNA text pattern
    :params GeneticCode: Genetic code dictionary
    :return: the translation of pattern into an amino acid string Peptide 
    '''
    k = 3
    translation = ""
    for i in range(0, len(pattern) - k + 1, k):
        subpattern = pattern[i:i+k]
        if geneticCode[subpattern] == "*":
            continue
        translation = translation + geneticCode[subpattern]
    return translation

def constructGeneticCode():
    geneticDict = {}
    with open("RNA_codon_table.txt") as geneticCode:
        codes = geneticCode.readlines()
        for code in codes:
            code = code.split(" ")
            if len(code[1].strip()) == 0:
                code[1] = "*"
            geneticDict[code[0]] = code[1].strip()
    return geneticDict

if __name__ == "__main__":
    geneticDict = constructGeneticCode()
    print(translate("", geneticDict))

